// see here for windowed contrast: https://www.shadertoy.com/view/XlyfWd

void mainImage( out vec4 O, vec2 U )
{
    float x = U.x < iResolution.x/2. ? U.x : U.x - .5,
          y = ceil(U.y/iResolution.y*20.)/20.,
        //l =       x  *  (1.+y);
          l = (x+iTime)*  (1.+y);
    O = vec4( .5+.5* sin( 3.1419/2.*l ) );
  //O = pow(O,vec4(1./2.2));
}
