void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec4 noise = texture(iChannel0, uv);

    vec3 marble = vec3(0.8, 0.8, 0.8);
    vec3 black = vec3(0.1, 0.1, 0.1);

    float intensity = abs(noise[0] - 0.0) + abs(noise[1] - 0.25) + abs(noise[2] - 0.5) + abs(noise[3] - 0.75);
    float sinval = sin(uv.x * 6.0 + intensity * 15.0) * 0.5 + 0.5;

    vec3 c = mix(marble, black, sinval);
    fragColor = vec4(c,1.0);
}
