#define R(p, a) p = cos(a) * p + sin(a) * vec2(p.y, -p.x)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (iResolution.xy - fragCoord.xy * 2.0)/min(iResolution.x, iResolution.y);
    if(acos(abs(uv.x))<.2||acos(abs(uv.y))<1.){fragColor.rgb=vec3(0.);return;};
	float tm =  mod(ceil(iTime*.2),2.)/.7*sin(iTime);
	float u = (.9*sin(abs(uv.x-tm)));
	if(tm>0.)uv.y*=-1.;
	float v = dot(uv.y*1.25,cos(uv.y)*.3+.4);
	if(ceil(sin(iTime*7.)/sin(iTime))>2.5)v=fract(uv.y);
	vec3 rd = (vec3(u,v,-2.));
	R(rd.xy, tm*.081 );
	vec3 p = vec3(0);
	vec3 p2 = vec3(0.);
	float t = 0.0, d = 0.0,d2=0.;
	for (float i= 0.;(i<1.0);i+=0.0078){
		d = min(abs(.001/.9+sin(3.+p.z*.1)*abs(cos(p.y*.9*p.y))*(cos(p.x*.5)) ),.01/abs((cos(p.x*p.x*0.65)*sin(p.y*p.y*0.4))));
		d2 = min(abs(.001/1.+sin(1.105+p2.z*.8)*abs(cos(p.y*.5))*(cos(p2.x*.5)) ),.01/abs((cos(p2.x*p2.x*0.35)*sin(p.y*p.y*0.4))));
		t += d;
		p+= rd*d;
		p2+=rd*d+step(.02,d2);
	};
	vec3 c = pow((vec3(.04)+(d-.02)*4.+t*.02+t*vec3(.12,.2,.19)*(0.85*-rd.y*.2)), vec3(1./2.));
	vec3 c2 = pow((vec3(.04)+(d2-.02)*4.+t*.02+t*vec3(.12,.2,.24)*(-rd.y*.3)), vec3(1./2.));
	fragColor.rgb = mix(c, c2,.1);
	fragColor.a = 1.0;
}
