void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy - vec2(.5);
    uv.y *= iResolution.y/iResolution.x;
    uv *= 8.+cos(iTime)*8.;

    float sy = sin(uv.y);
    float cx = cos(uv.x);
    uv.x += sy/cx;
    uv.y += cx/sy;

    float len = length(mod(uv,1.)-vec2(.5));
    if (len < 1.) {
        fragColor = vec4(len);
    } else {
        fragColor = vec4(0.);
    }
}
