// edit of http://glslsandbox.com/e#18752.0
uniform vec2 mouse;




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float gTime = iTime*0.5;
	float f =10., g=.005, h=.2;
	vec2 res = iResolution.xy;
	vec4 mou = iMouse;

	mou.x = sin(gTime * .73)*sin(gTime * .217) * 2. + sin(gTime * .34);
	mou.y = (1.0-cos(gTime * .632))*sin(gTime * .2131)*.5+cos(gTime * .3);
	mou = vec4(vec2((mou.xy+1.0) * res),vec2(0.1));
	vec2 z = ((-res+2.0 * gl_FragCoord.xy) / res.y);
	vec2 p = ((-res+2.0+mou.xy) / res.y);
	for( int i = 0; i < 2; i++) 
	{
		float d = dot(z+h,z-h);
        float h = h-sin(gTime*0.1);
        z = (vec2( dot(z.y,1.-h), dot(z.x,h+h) ) / d) + p*p; 
        z.x +=  0.5-abs(z.x*p.x);
        z.y -=  abs(z.y*p.y)*0.5;
		f = max( f-d, (1./dot(z+p,z+p) ));
		//g = min( g/d, sin(dot(z-p,z-p))+1.0);
	}
	f = abs(-log(f) / 3.7);
	g = abs(-log(g) / 8.0);
	fragColor = vec4(min(vec3(g-mou.z, g*f, f), 1.0),1.0);   
}
