//---------------------------------------------------------
// Shader:   StrangeSound.glsl                   2015-05-15
//---------------------------------------------------------

// This is a copy of the sound generator.
vec2 mainSound( in int samp, float time )
{
    time *= 0.1;
    float f = 110.0+mod(220.0+330.0*sin(0.1*time), 110.0);
    f = mod(f*5.0, 55.0);
    float im = 20.0 + 18.0*sin(0.21*6.2831*time);
    float x = sin( 6.2831*f*time + im*sin(0.25*6.2831*f*time) );
    float y = x * exp(-2.0*fract(8.0*time));
    return vec2(y*(1.0-sin(4.0*time)), y*sin(4.0*time));
}

#define AMP 12.0
#define FREQ 1.1
#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int samp = 0;
	vec2 ms = mainSound(samp,iTime + fragCoord.x / iResolution.x *0.04 *PI);
    float a1 = (fragCoord.y / iResolution.x -0.28) * AMP;
    fragColor = vec4(1.0 - abs(a1 - ms.x * 0.93) * FREQ
                    ,1.0 - abs(a1 - ms.y * -0.82) * FREQ
                    ,1.0 - abs(a1 - ms.y * 0.55) * FREQ, 1.0);
}

