float random( vec2 p) {
    return fract(sin(dot(p.xy,vec2(12.9898,78.233)))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec4 yellowish = vec4(0.9254901961, 0.6549019608, 0.0, 1.0);
    vec4 orangish = vec4(1.0, 0.3098039216, 0.0, 1.0);

    vec2 uv = fragCoord.xy / iResolution.xy;
    float cellWidth = 3.0;
    vec2 cellRowCol = floor(fragCoord / cellWidth);

    vec4 gradientNoise = vec4(step(uv.x, random(cellRowCol)));

    yellowish *= gradientNoise;
    orangish *= 1.0 - gradientNoise;

	fragColor = yellowish + orangish;
}
