//Ability to change number of rows and columns
float numRows = 10.0;
float numColumns = 15.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //setting colors for tiles
    vec4 wTile = vec4(1.0,1.0,1.0,1.0);
    vec4 bTile = vec4(0.0,0.0,0.0,1.0);

    //calculates the size of the square depending on the amount of rows and columns
    float board = (floor(uv.x * numColumns) + floor(uv.y * numRows));

    //creates the actual board
    if (mod(board, 2.0) == 0.0)
    {
        fragColor = wTile;
    }
    else
    {
        fragColor = bTile;
    }
}
