void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch(iChannel0,ivec2(U),0);   // O.xy = scrambled uv, O.z = scrambled cell col

    if (O!=vec4(0))
      O = mix( .6 + .6 * cos( O.z  + vec4(0,23,21,0) ), // rainbow(id)
               textureLod(iChannel1, O.xy,0.),          // scrambled video
               .6+.4*sin(.5*iTime)
             );
}

#define T(U) texelFetch(iChannel0,ivec2(U),0)

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, U,
          N = floor(15.*R/R.y);         // number of tiles in height/width
    float S = floor(R.y/N.y);           // tile size
          R = S * N;                    // game area
          U = floor((u-.5)/S);          // tile id

    if (iFrame==0) {                    // init state
        O = vec4( u/R, U.x+U.y, 0);
        return;
    }

    if ( u.x > R.x || u.y > R.y ) return;

    float r = iFrame % int(S) == 1 
                  ? fract(456.78*sin(17.751*float(iFrame))) // new scrambling
                  : T(0).a ,                                // current scrambling 
          i = (N.x+N.y+2.) * r -N.x-1., // sign(i) = lin vs col
          s = sign(fract(i)-.5);        // s = dir
          i = floor(i);                 // floor(i) = n (0 ignored since ambiguous)

    vec2 D = vec2( U.y == i-1. , U.x ==-i-1. );
    O = T( mod( u+s*D, R) );            // copy or scroll

    if ( u == vec2(.5) ) O.a = r;       // save scrambling info
}
