void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5);

    float z = 30.-pow(mod(iTime*.4,5.48),2.);
    uv += vec2(sin(iTime*10.)*.037,cos(iTime*5.)*.04)/z;
    uv.y *= iResolution.y/iResolution.x;

    vec3 clouds = vec3(0.);
    if (z > 10.) {
        clouds = texture(iChannel1,uv*z*.1).xxx*texture(iChannel1,uv/z).xxx;
        if (z < 15.) {
            clouds *= (z-10.)/5.;
        }
    }

    if (length(uv) < .04/z) {

        fragColor = vec4(clouds,1.);

    } else {

        vec3 samp = texture(iChannel0,uv*z).xyz*.3 + texture(iChannel1,uv*z).xyz + clouds;

        if (z > 29.) {
            samp *= 30.-z;
        }

        fragColor = vec4(samp,1.);

    }
}
