//Experiment by @bzgeb
//Based on previous shader: Overly satisfying by nimitz (twitter: @stormoid)

#define time iTime*2.6
#define pi 3.14159265

#define NUM 20.

float aspect = iResolution.x/iResolution.y;
float w = 50./sqrt(iResolution.x*aspect+iResolution.y);

mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}
mat2 m2 = mat2( 0.970,  0.242, -0.242,  0.970 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy*2.-1.;
	p.x *= aspect;
    p*= 1.05;
    vec2 bp = p;

    p *= mm2(time*.25);

    float lp = length(p);
    float id = floor(lp*NUM+.5)/NUM;

    p *= mm2(id*11.);

    p.y = abs(p.y); 

    //polar coords
    vec2 plr = vec2(lp, atan(p.y, p.x));
	fragColor = vec4(plr, 0.0 ,1.0);
}
