renderpass.code:
vec2 ScreenRatio = iResolution.xy/iResolution.y;
float t = iTime * 2.;
float bounceFac = t/10.;

//float bounceCut = mod(t * 5., 1000.) / 20.0;
//float bounceCut = 6.83*2. + 30.;
float bounceCut = t * 1. + 30.;

vec3 ColorBySeed(float Seed){
    vec3 color = sin(Seed + t +vec3(0,.33,.66)*6.28)*.5+.5;
    color -= mod(color,0.25);
    return color; 
}

vec2 PositionBySeed(float Seed){
    return vec2(cos(t+Seed),sin(t+Seed)) * (abs(0.5 - mod(bounceFac + Seed/bounceCut, 1.))) * 2.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 Scaled = (fragCoord/iResolution.xy*2.-1.)*ScreenRatio;

    vec3  Color    = vec3(0);
    for(float p=0.;p<510.;p++){
        Color += (ColorBySeed(p)/distance(Scaled,PositionBySeed(p))/20.);
    }
	Color /= 64.;

	fragColor = vec4(Color,1.0);
}
