const float brightness = 3.;
const int iterations = 84;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy - .5;

    float ang = atan(uv.y,uv.x);
    float len = length(uv);

    vec3 c = vec3(0.);

    for (int i = 0; i < iterations; i++) {
        float wlen = len*(35.+cos(iTime+float(i)*.723)*10.) + iTime;
        vec2 vuv = vec2(cos(wlen),sin(wlen))*len;
        float o = max(0.,1.-length(uv-vuv)*(5.+cos(float(i)*.6345)*3.));
        c += o*(vec3(sin(float(i)),cos(float(i)),-cos(float(i)))*.5+.5);
        uv = uv-uv*vuv/len*.4;
    }

    fragColor = vec4(c*(1./float(iterations))*brightness,1.);

}
