//Created: 2015-09-18
//Edit: 2019-07-20: There were mistakes in grammar (“have” instead of “has”, “is an option” instead of “included”), and I also don't like my old coding style with these whitespaces so I removed them.

float grid(float x,float div){
	return floor(x*div)/(div-1.);
}

//A computer has 16,777,216 (256^3) colors (0 included).
vec3 show_colors(vec2 uv,float colors){
	return vec3(grid(fract(uv.x*pow(colors,2.)),colors),grid(fract(uv.x*colors),colors),grid(uv.x,colors));
}
vec3 show_colors2(vec2 uv,float colors){
	return vec3(grid(fract(uv.x*colors),colors),grid(fract(uv.x),colors),grid(uv.y,colors));
}
vec3 show_colors3(vec2 uv,float colors){
	return vec3(fract(uv*colors),grid((uv.x/colors)+(floor(uv.y*colors)/colors),pow(colors,2.)));
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=fragCoord.xy/iResolution.xy;
	float time=iTime,
	      a=time*2., //Speed
	      b=10., //0 ... b
	      c=mod(floor(a),b)+2., //Display from 0 to b
	      d=mod(floor(a/b),3.); //Choose function
	vec3 e; //Out
	if(d==0.){e= show_colors(uv,c);}
	if(d==1.){e=show_colors2(uv,c);}
	if(d==2.){e=show_colors3(uv,c);}
	fragColor=vec4(e,1.);
}
