// Ray-Marching Pebbles Texture - 2017 by JT.

float sceneDist(vec3 p)
{
    //return 1.0 - texture(iChannel0, p.xy).r - p.z; // OUT;
    //return texture(iChannel0, p.xy).r - p.z; // IN
    float l = .125*sin(.1*iTime) + .375;
    return clamp(texture(iChannel0, p.xy).r + l, 0.0, 1.0) - l - p.z;
    //return clamp(1.0 - texture(iChannel0, p.xy).r + l, 0.0, 1.0) - l - p.z;
}

vec3 sceneNrm(vec3 p)
{
    vec3 d = vec3(0.001, 0, 0);
    //float dx = texture(iChannel0, p.xy + d.xy).r - texture(iChannel0, p.xy - d.xy).r;
    //float dy = texture(iChannel0, p.xy + d.yx).r - texture(iChannel0, p.xy - d.yx).r;
    float dx = sceneDist(p + d.xyz) - sceneDist(p - d.xyz);
    float dy = sceneDist(p + d.xyz) - sceneDist(p - d.yxz);

    return normalize(vec3(dx, dy, 0.002));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec3 l = normalize(vec3(cos(iTime),sin(iTime),1));
    vec3 l = normalize(vec3(0, 0,.1));
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 p = 2.0 * vec3(uv - 0.5, 0.0);
    p.x *= iResolution.x / iResolution.y;
    p *= 0.5;
	vec3 v = vec3(p.xy * 0.25, 1.0);
    p.x += iTime * 0.05;


    vec3 c = vec3(0,0,1);
    vec3 n = vec3(0);

    float t = 0.0;
    if(sceneDist(p) > 0.0)
    {
        int i;
        for(i = 0; i < 255; i++)
        {
            float d = sceneDist(p + v * t);
            if(d <= 0.01)
            {
                c = vec3(1);
                vec3 n = sceneNrm(p + v * t);
                c = n * 0.5 + 0.5;
                //c *= dot(n, l);
                c = c * dot(n, l) + pow(dot(n, l), 5.0);
                //c = mix(c * dot(n, l), texture(iChannel1, p.xy).rgb, vec3(pow(dot(n, l), 5.0)));
                break;
            }
            t += d;
        }
    }
    else
    {
        c = vec3(0); // viewer inside object
    }

    fragColor.rgb = sqrt(c); // adjust gamma
    //fragColor.rgb = vec3(t);
	//fragColor.rgb = vec3(float(i)/255.0, 1.0 - float(i)/255.0, 0.0);
}
