#define HALF_PI 1.57f
#define HARD_EDGE
float modulo(float a, float b)
{
    return a - b*floor(a/b);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;
    float level = abs(uv.x - 0.5) + abs(uv.y - 0.5);
    float time_cropped = modulo(iTime, HALF_PI);
    float sin_time = abs(sin(time_cropped))*2.0f - 1.0f;
    float brightness = min(level + sin_time, 1.0f);
#ifdef HARD_EDGE
    // = if(brightness < 0.2) brightness = 0.0
	brightness = brightness*(max(brightness - 0.2f, 0.0f)/(brightness - 0.2f));
    // = if(brightness > 0.7) brightness = 1.0
	brightness = brightness+(max(brightness - 0.7f, 0.0f)/(brightness - 0.7f));
#endif
    brightness = clamp(brightness, 0.0f, 1.0f);
    vec3 white = vec3(1.0f);
 fragColor = vec4(color  + white* (1.0f - brightness), 1.0);
}
