//Jimmy Griffiths
//Reveal

//Set up the Blur matrix
mat3 blur = mat3(
0.111, 0.111, 0.111, // first column (not row!)
0.111, 0.111, 0.111, // second column
0.111, 0.111, 0.111 // third column
);

//Set up the Sharpen matrix
mat3 sharpen = mat3(
0., -1, 0.0, // first column (not row!)
-1, 5, -1, // second column
0., -1, 0.0 // third column
);

//convert from screen space to texture space. 
//And, know which channel to take from
vec3 aSample(const int x, const int y, in vec2 fragCoord, int channel)
{
	vec2 uv = fragCoord.xy / iResolution.xy * iChannelResolution[channel].xy;
	uv = (uv + vec2(x, y)) / iChannelResolution[channel].xy ;
    switch(channel)
    {
    	case 0:   return texture(iChannel0, uv).xyz;
            //break;
        case 1: return texture(iChannel1, uv).xyz;
        	//break;

    }
	;
}

//Do the kernel on the appropriate texture
vec3 aFilter(in vec2 fragCoord, mat3 kernel, int channel)
{

	vec3 sum;
	for(int i = -1; i < 2; i++)
	{
		for(int j = 0; j < 3; j++)
		{
			sum += aSample(i, j - 1, fragCoord, channel) * kernel[i + 1][j]; 
		}
}

return sum;
}
	//Radius of the circle
    float Radius = (50.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Set up layers
    vec4 Layer1, Layer2;
    //Correct for aspect ratio

    //Do the kernels
    vec2 fc = fragCoord.xy;
    vec3 cf = aFilter(fc, sharpen, 0);
    Layer2 = vec4(cf, 1.0);

    vec3 cf2 = aFilter(fc, blur, 1);
    Layer1 = vec4(cf2, 1.0);

    //Create the circle. Set the alpha of Layer2 to 0 where the
    //circle is so that you can see through it.
    Layer2.a = step(length(fragCoord - iMouse.xy), Radius);

    //Interpolate the layers, and output the color.
    vec4 composite = mix(Layer1, Layer2, Layer2.a);
    fragColor = composite;
}
