vec4 changeColor()
{

    return vec4(0.9 * ceil(sin(iTime*1.0)) , 0.4* sin(iTime*20.0)/5.0 + 0.5 , 0.5  * abs(sin(iTime*0.01)),1);
}

float parabola( float x, float k )
{
    return pow( 4.0*x*(1.0-x), k );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 uv = fragCoord.xy / iResolution.xy;

    float compareValue =  (sin(iTime*1.0)+1.)*.35; //makes the thing move based on the RESULT, result is 0 - 0.8 
   float value = step(compareValue,uv.x) /* left */ - step(compareValue+.3,uv.x) /* right */; // makes the shape

    fragColor = vec4(value);

}
