float spread = 6.0;

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    float tiles = 150.0;

    vec2 chk = (floor(uv * tiles +0.5 ) / tiles);
    float r = smoothstep(0.0,0.9,rand(chk ));

    r = r * pow(sin(chk.x*3.14 ), spread);

	r = chk.x > 0.5 ? 1.0-r : r;

    vec4 t0 = texture(iChannel0, chk);
    vec4 t1 = texture(iChannel1, chk);
	fragColor = mix(t0, t1, r);
}
