// Created by James Jacob Kurian (jamesjacobk) Jan 2018

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 finalColor = vec3(0.0);
    float speed = 100.0;
    float magnitude = 1.0;
    uv.y = sin(iTime);
    magnitude = abs(2.0/(speed * uv.y));
    finalColor = vec3(magnitude, magnitude, 0.2 * magnitude); 
	fragColor = vec4(finalColor, 1.0);
}
