#define PI 3.14159
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //	correct aspect ratio distortion
	vec2 uv = fragCoord / iResolution.y;

    //	-----	MATHS	-----	//
    float c = 0.0;

    //	shift sectors up and down
    float direction = sign( sin( uv.x * 25.0 + 1.5 ) );
    uv.y += sin( iTime * direction );

    //	create "circles" by shading pixel according to sin/cos then applying as a mask
    float periodx = ( uv.x * 50.0 );
    float periody = ( uv.y * (50.0 ) );
    c += cos( periodx );
    c += sin( periody );


	fragColor = vec4( c );
}
