// Alexander Lemke, 2017

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{  
    vec2 uv = fragCoord.xy / iResolution.yy;

    float z = 1.0 + cos(iTime * 0.125) * 0.99;
    vec2 o = (iMouse.z > 0.0) ? iMouse.xy / iResolution.xy * vec2(2.0) - vec2(2.2, 1.3) : -vec2(1.2, 0.32);    
    vec2 c = (uv - vec2(0.5)) * z + o;

    vec2 v = vec2(0);
    float m = 0.0; 
    const float r = 5.0;   	    
    for (int i = 0; i < 256; ++i)
    {
        v = vec2(v.x * v.x - v.y * v.y, 2.0 * v.x * v.y) + c;
        m = (dot(v, v) < (r * r)) ? (m + 1.0) : m;
        v = clamp(v, -r, r);
    }  
    fragColor = (int(m) != 256) ? sin(m / vec4(4, 8, 12, 1)) * 0.25 + 0.75 : vec4(0);
}
