///////////
// Image //
///////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float speed	  = 1.3;
    float heightA = 0.05;
    float heightB = 0.05;
    float heightC = 0.03;

    vec2 xy = fragCoord.xy;				    // We obtain our coordinates for the current pixel

    xy.x = xy.x / iResolution.x;		    // We divide the coordinates by the screen size (get a value between 1 and 0
    xy.y = xy.y / iResolution.y;

    xy.y = sin((xy.x + (-iTime*speed))*5.0)*heightA + xy.y;
    xy.y = xy.y + sin((xy.x + (iTime))*3.0)*heightB;
    xy.y = xy.y + sin((xy.x + (iTime))*2.0)*heightC;     


    vec4 texColor = texture(iChannel0,xy); // Get the pixel at xy from iChannel0

    fragColor = texColor;   				 // draw the image to the screen

}
