// Image A = C
// Blur, emboss, displacement
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 scale = vec2(1,1) / iResolution.xy;
	vec2 uv1 = fragCoord.xy;

    vec4 vid1 = texture(iChannel1, (uv1 + vec2(-5, 0)) * scale);
    vec4 vid2 = texture(iChannel1, (uv1 + vec2(+5, 0)) * scale);

    vec4 emboss = (vid2 - vid1);

    vec2 displ = emboss.rb * 0.2; 

    vec2 uv2 = uv1;
    uv2 += vec2(-1.0,-1.0);
    fragColor = texture(iChannel0, uv2 * scale + displ);

    vec2 uv3 = uv1;
    uv3 += vec2(1.0,-1.0);
    fragColor += texture(iChannel0, uv3 * scale + displ);

    vec2 uv4 = uv1;
    uv2 += vec2(1.0,1.0);
    fragColor += texture(iChannel0, uv4 * scale + displ);

    vec2 uv5 = uv1;
    uv3 += vec2(1.0,-1.0);
    fragColor += texture(iChannel0, uv5 * scale + displ);

    fragColor *= 0.25;

	fragColor += emboss * 0.1;
}

// A: 0=noise, 1=B
// Seed, ping
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    if(fragCoord.y < 2.0)
        fragColor = texture(iChannel0, vec2(fract(uv.x + iTime * 311.0), 0));
    else
        fragColor = texture(iChannel1, uv);
}

// B: 0=A
// Pong, average, scroll
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 scale = vec2(1,1) / iResolution.xy;
	vec2 uv1 = fragCoord.xy;
    uv1.y -= 6.0;
    fragColor = texture(iChannel0, uv1 * scale);

    vec2 uv2 = uv1;
    uv2 += vec2(-2.0,-2.0);
    fragColor = texture(iChannel0, uv2 * scale);

    vec2 uv3 = uv1;
    uv3 += vec2(2.0,-2.0);
    fragColor += texture(iChannel0, uv3 * scale);

    vec2 uv4 = uv1;
    uv2 += vec2(2.0,2.0);
    fragColor += texture(iChannel0, uv4 * scale);

    vec2 uv5 = uv1;
    uv3 += vec2(2.0,-2.0);
    fragColor += texture(iChannel0, uv5 * scale);

    fragColor *= 0.99 * 0.25;
}

// C: 0=A
// Color-mapping
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float i = texture(iChannel0, uv).r;
    i = pow(i, 1.5);
    if(i > 0.4)
        fragColor = mix(vec4(1,1,0,1), vec4(1,0.5,0,1), 1.0-(i-0.4)*1.66);
    else if(i > 0.3)
        fragColor = mix(vec4(1,0.5,0,1), vec4(1,0,0,1), 1.0-(i-0.3)*10.0);
    else if(i > 0.2)
        fragColor = mix(vec4(1,0,0,1), vec4(0.5,0.5,0.5,1), 1.0-(i-0.2)*10.0);
    else
        fragColor = mix(vec4(0.5,0.5,0.5,1), vec4(0,0,0,1), 1.0-i*5.0);
}
