vec3 g = vec3(.5);
vec3 w = vec3(1.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    uv *= 20.;

    float m = mod(uv.y,2.);
    uv.x += step(1.,m)*iTime*.5;

    vec2 f_uv = fract(uv);
    vec2 i_uv = floor(uv);

    f_uv.y += step( 1.5/iResolution.x*20., f_uv.y);

    float c = mod(i_uv.x,2.);
    float d = mix(g,w,f_uv.y).x;

    vec3 col = vec3(c*d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
