#define PI 3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    float r = length(pos);

    float theta = iTime + sin(r * 12.0) * 0.04;
    float cosTheta = cos(theta);
    float sinTheta = sin(theta);
    mat2 m = mat2(cosTheta, -sinTheta,
                  sinTheta, cosTheta);

    pos = m * pos;

    float angle = atan(pos.y, abs(pos.x));

    vec2 uv = vec2(r - iTime, angle/PI);

    float l = length(uv);

    float t = 25.0;

    float time = iTime;

    float a[3];

    float c = 0.0;

    for (int i = 0; i < 3; i++)
    {

    float s = l;

    float tuv = uv.x;
    uv.x = uv.x - uv.y;
    uv.y = uv.y +tuv;

    s += (sin(uv.x * t * 0.2));

    s += (sin(uv.y * t * 0.2));

    float m = mod(s + (time + c) * 0.2, 0.5);

    //m *= mod(r - iTime * 0.2, 0.5);

    a[i] = m;    

    c += sin(iTime);

    }

    vec3 col = vec3(a[0], a[1], a[2]);

    col *= r;
    col = 1.0 - exp(-col);

    // Output to screen
    fragColor = vec4(col,1.0);
}
