void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; // Origen esta en el borde inferior izq, como lo pongo en el centro?
	// Cambiando cordenadas al centro de la pantalla
    vec2 uv2 = uv - 0.5;

    vec3 color = vec3(1.);

    float radius = 0.3;

    if (radius > length(uv2))
    {
    	color = vec3(0., 1., 0.);
    }

    fragColor = vec4(color, 1.0);
}
