void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float scale = max(iResolution.x,iResolution.y);
  	vec2 uv = fragCoord.xy / scale;
    vec2 mouse = iMouse.z>0.0?iMouse.xy:vec2(-100,-100);
    fragColor = distance(mouse / scale,uv) < 0.02?vec4(1.0,1.0,1.0,1.0):texture(iChannel0, fragCoord.xy/iResolution.xy);


    vec3 d = vec3(0.0,0.0,0.0);
    float n = 0.0;

    for(float dx=-1.0;dx<=1.0;dx+=0.5) {
	    for(float dy=-1.0;dy<=1.0;dy+=0.5) {
		    vec2 pq = (fragCoord.xy-iResolution.xy*0.5)/iResolution.xy;
            float s = 0.004;
	    	d.r += texture(iChannel0, 0.5+(pq+vec2(dx,dy)*s)/1.005).r;
	    	d.g += texture(iChannel0, 0.5+(pq+vec2(dx,dy)*s)/1.006).g;
	    	d.b += texture(iChannel0, 0.5+(pq+vec2(dx,dy)*s)/1.008).b;
            n+=1.0;
    	}
    }
    fragColor.rgb = mix(fragColor.rgb, d/n*0.99, 0.1);
    fragColor = clamp(fragColor,0.0,1.0);
}
