void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float pixelShift = .5 + .5*sin(iTime);

    vec4 pictureColor = texture(iChannel0, uv);

    float colorMagnitude = length(pictureColor.xyz) / sqrt(3.);

    if (uv.x < pixelShift)
    {
    	fragColor = vec4(colorMagnitude, colorMagnitude, colorMagnitude, 1.0);
    }
    else
    {
        fragColor = pictureColor;
    }
}
