void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 a = vec2(-10.0, 10.0);
    vec2 b = vec2(+10.0, 10.0);
    vec2 c = vec2(+20.0, 0.0);
    vec2 d = vec2(-20.0, 0.0);

    vec2 fx1 = mix(a, b, uv.x);
    vec2 fx2 = mix(d, c, uv.x);
    vec2 V = mix(fx2, fx1, uv.y);

    vec2 grid = abs(fract(V - 0.5) - 0.5) / fwidth(V);
    float line = 1.0 - min(grid.x, grid.y);

	fragColor = vec4(vec3(line), 1.0);
}
