void mainImage( out vec4 O, vec2 u )
{
    float t = fract(.2*iDate.w);
    vec2  R = iResolution.xy,
          U = (u-.5*R)/R.y;
    O = floor( vec4(  U *= 10. / pow(10.,t) , U*10.) );

	O =   (.9+.1 *sin(6.28*t +length(u)/R.y + vec4(0,2.1,-2.1,0)) )
        * mix( mod(O.x+O.y, 2.), mod(O.z+O.a, 2.), t);
}
