//Float determines size of each checker (out of 1)
float checkerSize = 0.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Checker colors
    vec4 col1 = texture(iChannel0, uv);
    vec4 col2 = texture(iChannel1, uv);

    //Correct spacing for each checker
    if(mod(uv.y,checkerSize) > checkerSize/2.0)
    {
        if(mod(uv.x,checkerSize) > checkerSize/2.0)
    	{
			fragColor = col1;
    	}
        else
        {
			fragColor = col2;
        }
    }
	else
    {
        if(mod(uv.x,checkerSize) > checkerSize/2.0)
    	{
			fragColor = col2;
    	}
        else
        {
            fragColor = col1;
        }
    }
}
