void mainImage( out vec4 O, vec2 U )
{
	vec2 V = floor( U *= 10./ iResolution.y );
	U = 2.*fract(U)-1.;

    float g = mod(V.x+V.y, 2.),
          a = atan(U.y,U.x)+iDate.w*sign(g-.5), 
          l = length(U),
          c = step(.3+.3*sin(a*5.), l);

	O = mix(vec4(1,c,l,1),vec4(0,0,c,0), g )
        * (.9+.1*sin(20.*a)); 
}
