void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    float gridX = 6.0;
    float x = mod(floor(uv.x / (iResolution.x / gridX)), 2.0);

    float gridY = 6.0;
    float y = 1.0 - mod(floor(uv.y / (iResolution.y / gridY)), 2.0);


    fragColor = vec4(1.0) * (x);


    float dy = mod(floor(uv.y / (iResolution.y / 12.0)), 2.0);
    if (dy == 1.0)
    {
		 fragColor = vec4(1.0) * (x);
    }
    else
    {
		 fragColor = vec4(1.0) * (y);
    }
}
