/********************************************************************
Blinn-Phong Shading

Author : Sakib Saikia 1/12/2017
********************************************************************/

#define PI 3.141592

vec3 spherePos = vec3(0.0,0.0,0.0);
float sphereRadius = 1.0;
vec3 albedo = vec3(1.0,0.0,0.0);
vec3 bgColor = vec3(0.6,0.6,0.6);
vec3 eye = vec3(0.0,0.0,-2.0);
vec3 ambient = vec3(0.02);
vec3 spec = vec3(1.0);
float gloss = 50.0;

// Construct inverse LookAt matrix. Convert from view space to world space
// eyePos 	- eye/camera position in World Space
// v 		- LookAt position in world space
mat3 invLookAt(vec3 eyePos, vec3 v)
{
    vec3 worldUpDir = vec3(0.0,1.0,0.0);
    vec3 viewLookDir = normalize(v - eyePos);
    vec3 viewRightDir = normalize(cross(worldUpDir, viewLookDir));
    vec3 viewUpDir = normalize(cross(viewLookDir, viewRightDir));

    return mat3(viewRightDir, viewUpDir, viewLookDir);
}

vec3 getLightDir()
{
    float azimuth = 0.25 * PI;
    float zenith = 0.5 * PI + iTime;
    return vec3(cos(azimuth) * sin(zenith), sin(azimuth) * sin(zenith), cos(zenith));
}

// Sphere ray trace
// p - ray trace origin in world space
// d - ray direction in world space
// o - sphere origin in world space
// r - sphere radius
// See : http://www.cs.virginia.edu/~gfx/Courses/2008/AdvancedGraphics/lectures/lecture07_implicit.pdf
float rayTrace(vec3 p, vec3 d, vec3 o, float r)
{
    vec3 op = p - o;

    float a = 1.0;
    float b = 2.0 * dot(d, op);
    float c = dot(op, op) - r * r;

    float t = b*b - 4.0*a*c;
    if(t >= 0.0)
    {
        return (-b - sqrt(t))/(2.0 * a);
    }
    else
    {
        return -1.0;
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 pixelColor = bgColor;

    // NDC
    vec2 p = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    // Inverse View matrix
    mat3 viewMat = invLookAt(eye, spherePos); 

    // Ray direction in world space
    vec3 rayDir = viewMat * normalize(vec3(p.xy, 0.75));

    // Ray trace!
    float t = rayTrace(eye, rayDir, spherePos, sphereRadius);

    if(t > 0.0)
    {
        vec3 pos = eye + t * rayDir;
        vec3 n = normalize(pos - spherePos);
        vec3 l = normalize(getLightDir());
        vec3 v = normalize(-eye);
        vec3 h = normalize(l - v);

        // -- Ambient 
        pixelColor = albedo * ambient;

        // -- Diffuse 
    	pixelColor += albedo * max(dot(n, l), 0.0);

        // -- Specular
        // Use clamp() instead of max() as dot product between normalized vectors
        // can be above 1.0 due to floating point imprecision. pow() can make those errors worse.
        // See iq's comment here https://www.shadertoy.com/view/XtGXW1
        pixelColor += spec * pow(clamp(dot(n,h), 0.0, 1.0), gloss);
    }

    vec3 gamma = vec3(1.0 / 2.2);
    fragColor = vec4(pow(pixelColor,gamma),1.0);
}
