void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	for(int i = 0; i < 100; i++) {
        vec2 uv = fragCoord.xy / iResolution.x;
        uv.x -= 0.5;
        uv.y -= iResolution.y/iResolution.x/2.0;
        float time = sin((iTime+15.0)/3.0)*2.5+4.5;
        uv *= 10.0/pow(time,time);
        uv.x += 0.5;
        uv.y += iResolution.y/iResolution.x/2.0;
        uv.x += -0.1014;
        uv.y += 0.03115;
        vec2 c = uv; 
        float good = 0.0;
        for(int n = 0; n < 3000; n++) {
            uv = mat2(uv,-uv.y,uv.x) * uv + c;
            if(dot(uv,uv)<2.0) {
                good += 0.01;
            }
        }
        fragColor = vec4(cos(good+3.1415926),sin(good),floor(good/30.0),1);
    }
}
