void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float radius = 75.0;
    float distortion = clamp(1.0-pow((distance(iMouse.xy,fragCoord)/radius),2.5),0.0,2.0);

    uv = mix(uv,iMouse.xy/iResolution.xy, distortion);
    // Output to screen
    vec4 img = texture(iChannel0,uv);
    float black =  1.0-distortion;
   	img = img * vec4(black);
    fragColor = img;
}
