// Draw


const float buttonTransparency = 0.2;
#define lettersOnTexture vec2(16, 16)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // draw cells
    ivec2 c = ivec2(fragCoord);
    c /= cellSize;

    fragColor.rgb = vec3( alive(c) ? 1. : 0.);

    if (	// draw button
        fragCoord.x < pauseButtonSize.x && 
        fragCoord.y < pauseButtonSize.y 
    ) {
        bool pause = paused;
        vec2 symbol = vec2(pause ? 6 : 5, 15);
        vec4 image = texture(
            iChannel1,
            (fragCoord/pauseButtonSize + symbol)/lettersOnTexture
        );
        float val = image.r;

        vec3 buttonCol = pause ? vec3(1., 1. - val, 0) : vec3(0, 1. - val, val);
        //buttonCol = texel;

        fragColor.rgb = mix(buttonCol, fragColor.rgb, buttonTransparency);
    }

    //fragColor.a = 1.;
}

// Cellular automation


int countNbs(ivec2 c) {	// Counts 8 surrounding pixels

    int count = 0;

    #define countNb(x, y) if ( alive(c + ivec2(x, y)) ) { if (count >= 3) { return 4; } count++; }

    countNb(-1, -1);
    countNb(-1, 0);
    countNb(-1, 1);

    countNb(0, -1);
    countNb(0, 1);

    countNb(1, -1);
    countNb(1, 0);
    countNb(1, 1);

    #undef countNb

    return count;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 c = ivec2(fragCoord);	// cell

    vec2 afterScale = vec2(c * cellSize);
    if (	// outside screen
        afterScale.x > iResolution.x ||
        afterScale.y > iResolution.y
        || fragCoord.x > iResolution.y
    ) {
        return;
    }

    bool mouseDown = iMouse.z >= 0.;
    if (c == pausedAdress) {	// pause button
        bool pausePressed = 
            mouseDown &&	// mouse down
            iMouse.x <= pauseButtonSize.x &&
            iMouse.y <= pauseButtonSize.y;
        vec4 texel = texelFetch(iChannel0, pausedAdress, 0);

        if (pausePressed && texel.g < 1.) {	// just started pressing
            fragColor.b = texel.b > 1. ? 0. : 1.5 ;	// flip mode
            fragColor.g = on;
        } else if (!pausePressed && texel.g > 1.) {	// just stopped pressing
        	fragColor.b = texel.b;
            fragColor.g = off;
        } else {
            fragColor = texel;
        }

        return;
    }

    if (iFrame == 0) {	// init pattern
        #ifdef serpinski
        	fragColor.r = (c.x & c.y) > 0 ? off : on ;
        #else
        	fragColor = texelFetch(iChannel1, c % int(iChannelResolution[1].xy), 0) * 1.42;
        #endif

        return;
    }


    ivec2 m = ivec2(iMouse.xy) / cellSize;
    bool lives;
    if (mouseDown && ivec2(m) == c) {	// mouse spawn
        lives = true;
    } else if (paused) {	// pause
    	lives = alive(c);
    } else {	// game rules
        int count = countNbs(c);
    	lives = count == 3 || (alive(c) && count == 2);
    }

    fragColor.r = lives ? on : off ;
}

//#define serpinski

#define cellSize 4	//int(exp2(1.) + 0.5)

#define on 1.5
#define bound 1.
#define off 0.

const vec2 pauseButtonSize = vec2(64);	// texture is 64
//const int pauseButtonSize2 = 32;
const ivec2 pausedAdress = ivec2(0);


#define alive(c) (texelFetch(iChannel0, c, 0).r >= bound)
#define paused texelFetch(iChannel0, pausedAdress, 0).b > bound
