void mainImage(out vec4 color, in vec2 coord)
{
    float m;
    vec2 uv = coord.xy / iResolution.xy;
    uv.x *= 2.;
    uv.x -= 1.;
    //uv.y *= 1.382;
    uv.y += .382;
    for (int i = 0; i < 33; ++i) {
        uv= abs(uv);
        m = uv.x * uv.x  + uv.y * uv.y;// - .0031;
        uv.x = uv.x/m - .217;
        uv.y = uv.y/m - .105;
    }
    color= vec4(vec3(m + .28), 1.);
}
