vec2 m(){
    vec2 m = iMouse.xy / iResolution.xy-.5; 
    m.x *= iResolution.x/iResolution.y;
	return m;
}


const float rad = 0.07;

float c(vec2 pos, float r)
{
    float s = smoothstep(r - 2.0, r, length(pos));
    return s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

    vec2 loc = uv2 - m().xy;

    vec3 col = texture(iChannel0, uv).xyz * 0.97;


    if(length(col) < 0.1 && length(col) > 0.005) 
      col = vec3(1.0, 0.0, 0.0); 

    	col = mix(vec3(1), col , c( loc.xy, rad));


    fragColor = vec4(col, 0.0);



}
