void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - vec2(1.0, 1.0);

    float normal = length(uv);
    float d = normal * 5.0;
    float time = fract(iTime * 0.1) * 5.0;

    float a = atan(uv.x, uv.y) / 3.1415926 * 0.5;

    float v = 1.0 - abs(fract(d - a) * 2.0 - 1.0);

    float x = 5.0 * normal - a;
    float tmp = a + floor(x) + 1.0;
    v *= step(tmp, time);
    v = pow(v, 10.);

    fragColor = vec4(v, v, v, 1.0);
}
