void mainImage( out vec4 O, vec2 u )
{
	vec2 R = iResolution.xy,
         U = u / R;
    float a = .5+U.x, 
        ofs = 2.*U.y/R.x;
    a = floor(10.*a) / 10.;

	O = textureLod(iChannel0, vec2( a*U.x+ofs, U.y) , 0.); // 1 resample at rate a

    if (U.y>.5)                                            // average of 2 resample at rate a
        O = .5*O + .5*textureLod(iChannel0, vec2( a*(U.x+1./R.x)+ofs, U.y) , 0.);

    if (fract(U.x*10.)<10./R.x || u.y-.5==R.y/2.) O = vec4(1,0,0,0); // red bars
}

void mainImage( out vec4 O, vec2 U )
{
    O = vec4( mod(U.x-.5,2.) );
}
