void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pos = uv * 2.0 - 1.0;

	uv -= vec2(0.5, 0.5);

    float c_dist = abs(sin(iTime * 0.3) / 2.0);// 0.3;
    float c_delta = 0.05;

    float dist = distance(uv, vec2(0.0, 0.0));
    if( abs(c_dist - dist) < c_delta ){
        float delta = c_delta - abs(c_dist - dist);
        float deg = atan(uv.y / uv.x);

        uv.x -= sqrt( delta) * abs(cos(deg));
        uv.y -= sqrt(delta) * abs(sin(deg));
    }

	fragColor = texture(iChannel0, uv+vec2(0.5, 0.5));
}
