void mainImage(out vec4 color, vec2 coord) {
    vec2 uv = coord / iResolution.xy;

    float w = (uv.x < 0.2 && uv.y < 0.2) ? 1.0 : 0.0;

    vec2 tex_res = vec2(textureSize(iChannel0, 0));
    float lowest_mip = log(min(tex_res.x, tex_res.y)) / log(2.0) + 1.0;

    color = textureLod(iChannel0, uv, lowest_mip * w);

    color = (uv.x < 0.1 && uv.y < 0.2) ? 1.0 - color : color;
}
