// Greg removed a few extra chars:
void mainImage(out vec4 o, vec2 u)
{
    u /= iResolution.xy;
    for (float i = 1.; i++ < 20.;)
        o = u.y < sin( ceil(2e2*u.x/i+i*i+iTime) ) - .04*i ? o-o+i/20. : o;
}

