void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float lightRange = 0.2, borderFadeAlpha = 0.1;

    vec2 pixel = fragCoord.xy / iResolution.xy;
    pixel.y = 1.0 - pixel.y;
    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse.y = 1.0 - mouse.y;
	vec4 texColor = texture(iChannel0, pixel);

    texColor.rgba *= borderFadeAlpha * floor(clamp((lightRange * 2.0) / distance(pixel, mouse), 0.0, 1.0)) + ((1.0 - borderFadeAlpha) * floor(clamp(lightRange / distance(pixel, mouse), 0.0, 1.0)));

    fragColor = texColor;
}
