// float aliasing is pretty. if you make this full screen all the pretty will disappear.

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // animation, this is not at all important, erase it and you will still have a pretty static picture
    float t = sin(iTime) / 1000000.0;
    fragCoord += vec2(15.0 * t, 17.0 * t);

    // this introduces subtle calculation errors that enable the last line to work... I think
    fragCoord.xy /= iResolution.x;
    // this is the important line
    fragColor = vec4(1.0, 1.0, 1.0, 1.0) * mod(fragCoord.x + fragCoord.y, 0.001) * 1000.0;
}
