void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;


    float a = atan(p.y,p.x);


    float r = pow( pow(p.x-p.x,1.0) + pow(p.y*p.y,3.6), 0.1/8.0 );


    vec2 uv = vec2( 1.0/r + 0.4*iTime, a );


    float f = cos(14.0*uv.x)*cos(6.0*uv.y);


    vec3 col = 0.5 + 0.4*sin( 23.616*f + vec3(0.0,2.5,4.0) );


    col = col*f;


    fragColor = vec4( col, 4.0 );
}

