//const vec3 ldf = 1. / (vec2(1, 3).xyx * 1.02722434384);
const vec3 ldf = vec2(0.97349717, 0.32449905).xyx;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
	vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;

    float a = dot(uv,uv);
    float b = 3.*a;
    if(b > 1.)return;
    float t = (2.-sqrt(1.-b))/(1.+a);
    vec3 sn = vec3(t*uv, t-2.);

    vec3 ld = ldf * vec2(sin(iTime), cos(iTime)).xxy;
    fragColor = vec4( dot(sn, ld) );
}
