void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  const float PI = 3.1415926;
  const float TWO_PI = PI * 2.;

  vec2 center = vec2(iResolution.x / 2.0, iResolution.y / 2.0);
  float angle_target = clamp(sin(iTime) * TWO_PI, 0.0, TWO_PI);
  float angle_current = atan(center.x - fragCoord.x, center.y - fragCoord.y) + PI;

  vec2 calculated_uv = fragCoord.xy / iResolution.xy;
  fragColor = texture(iChannel0, vec2(calculated_uv.x, -calculated_uv.y));
  fragColor.r = float(angle_target <= angle_current);
}
